    var mockingState: MockingState = MockingState.Success

    enum class MockingState {
        IOException, NullData, Success, SuccessEmptyData
    }

    override fun fetchNews(period: Int): Response<MostPopularNewsResult?>? = when (mockingState) {
        MockingState.IOException -> {
            throw IOException()
        }
        MockingState.NullData -> {
            null
        }
        MockingState.Success -> {
            val result: MostPopularNewsResult = Gson().fromJson(mockData, MostPopularNewsResult::class.java)
            Response.success(result)
        }
        MockingState.SuccessEmptyData -> {
            Response.success(MostPopularNewsResult(null))
        }
    }

    fun getFileFromPath(obj: Any, fileName: String?): File? {
        val classLoader = obj.javaClass.classLoader!!
        val resource: URL = classLoader.getResource(fileName)
        return File(resource.path)
    }

    companion object {
        private var newsRepository: MockNewsRepository? = null
        fun getInstance(service: MostPopularNewsService, apiKey: String): MockNewsRepository? {
            if (newsRepository == null) {
                newsRepository = MockNewsRepository(service, apiKey)
            }
            return newsRepository
        }
    }
